krpano.action:
set(layer['tipBg'].visible, false);
set(layer['tipHelp'].visible, false);
set(layer['tipImage'].visible, false);
set(layer['tipMove'].visible, false);
set(layer['tipOut'].visible, false);
set(layer['tipSound'].visible, false);
set(layer['tipZoomPC'].visible, false);
set(layer['tipZoomM'].visible, false);
stopdelayedcall('tipHelp');
stopdelayedcall('tipOut');
stopdelayedcall('tipSound');
stopdelayedcall('tipImage');
stopdelayedcall('tipMove');
stopdelayedcall('tipZoom');

set(layer['tipBg'].visible, true);
if(get(stagewidth) GT 1499,
    set(layer['tipMove'].x, -450);
    set(layer['tipMove'].y, -40);
    set(layer['tipZoomPC'].x, 450);
    set(layer['tipZoomPC'].y, -40);
    set(layer['tipZoomM'].x, 450);
    set(layer['tipZoomM'].y, -50);,

    set(layer['tipImage'].x, 25);
    set(layer['tipImage'].y, 0);
    set(layer['tipMove'].x, 25);
    set(layer['tipMove'].y, -190);
    set(layer['tipZoomPC'].x, 25);
    set(layer['tipZoomPC'].y, 190);
    set(layer['tipZoomM'].x, 25);
    set(layer['tipZoomM'].y, 190);
);

if(get(stagewidth) LT 500,
    set(layer['tipImage'].scale, 0.5);
    set(layer['tipMove'].scale, 0.5);
    set(layer['tipZoomPC'].scale, 0.5);
    set(layer['tipZoomM'].scale, 0.5);
    set(layer['tipImage'].x, 0);
    set(layer['tipMove'].x, 0);
    set(layer['tipMove'].y, -130);
    set(layer['tipZoomPC'].x, 0);
    set(layer['tipZoomPC'].y, 130);
    set(layer['tipZoomM'].x, 0);
    set(layer['tipZoomM'].y, 130);
);

if(get(stagewidth) LT 300,
    set(layer['tipImage'].scale, 0.3);
    set(layer['tipMove'].scale, 0.3);
    set(layer['tipZoomPC'].scale, 0.3);
    set(layer['tipZoomM'].scale, 0.3);
    set(layer['tipImage'].x, 0);
    set(layer['tipImage'].y, 0);
    set(layer['tipMove'].x, 0);
    set(layer['tipMove'].y, -130);
    set(layer['tipZoomPC'].x, 0);
    set(layer['tipZoomPC'].y, 130);
    set(layer['tipZoomM'].x, 0);
    set(layer['tipZoomM'].y, 130);,
    ,
);
delayedcall('tipHelp', 0.5, set(layer['tipHelp'].visible, true));
delayedcall('tipSound', 0.5, set(layer['tipSound'].visible, true););
delayedcall('tipOut', 0.5, set(layer['tipOut'].visible, true););
delayedcall('tipImage', 1, set(layer['tipImage'].visible, true););
delayedcall('tipMove', 1, set(layer['tipMove'].visible, true););
delayedcall('tipZoom', 1, if(device.desktop,
    set(layer['tipZoomPC'].visible, true);,
    set(layer['tipZoomM'].visible, true));
);
